export type MessageEventInterface = {
  addMessageEventListener: <T>(handler: (response: T) => void) => void;
  removeMessageEventListener: <T>(handler: (response: T) => void) => void;
};

export type PostMessageFn = <T>(message: T, targetOrigin: string) => void;
export type PostMessageInterface = {
  postMessage: PostMessageFn;
};
export type PostMessageTargetCallback = (instanceId: string) => PostMessageInterface;
