import { step } from 'mocha-steps';
import sinon from 'sinon';
import chai from 'chai';
import chaiSubset from 'chai-subset';
import chaiAsPromised from 'chai-as-promised';
import sinonChai from 'sinon-chai';
import { IntegrationTestHelpers, sleep } from '../testSupport';
import { GatekeeperAPIStatus } from '../../src/types/gatekeeperApi';
import { GatewayStatus } from '../../src';

chai.use(sinonChai);
chai.use(chaiSubset);
chai.use(chaiAsPromised);

const sandbox = sinon.createSandbox();

describe('GatewayClientCore refresh client sends rejected integration tests', () => {
  let testHelpers: IntegrationTestHelpers;

  const errorCode = '0x001';

  before(() => {
    testHelpers = new IntegrationTestHelpers(sandbox);
    // stub successful fetch calls with existing GK record
    testHelpers.stubSuccessfulFetchCalls({ withExistingGatekeeperRecord: true });
    // stub find token with an expired token
    testHelpers.stubFindToken({
      expiryTime: 1682891700, // old timestamp
    });
  });

  after(() => {
    testHelpers.cleanup();
    sandbox.restore();
  });

  afterEach(() => {
    testHelpers.expectFlowIdToBeSet('GWRC_refresh_');
  });

  step('1. expect the Gatekeeper client to successfully initialize', async () => {
    testHelpers.initializeGatewayCore();
    // wait for GK and on-chain checks to complete
    await sleep(110);
  });

  step('2. Expect gateway status to change to REFRESH_TOKEN_REQUIRED', async () => {
    testHelpers.expectRefreshTokenRequired();
  });

  step('3. Expect successful civic-sign public-key, did and POWO flows', async () => {
    await testHelpers.expectSuccessfulCivicSignProofFlow();
  });

  step('4 Simulate a successfull client sending of transaction when presented to the user', async () => {
    testHelpers.prepareSuccessfulHandleTransaction();
  });

  step('5. Simulate the gatekeeper rejecting the transaction', () => {
    testHelpers.simulateGatekeeperRequestTokenRejection(GatekeeperAPIStatus.REJECTED, errorCode);
  });

  step('6. Simulate refresh action being triggered', async () => {
    await testHelpers.simulateSucessfulDataCollectionTriggeringGKRefreshRequest();
  });

  step('7. Wait for the GK refresh request to complete', async () => {
    await sleep(120);
  });

  step(`8. Expect the status to change to REJECTED`, async () => {
    testHelpers.expectRejected(GatewayStatus.REJECTED);
  });

  step('9. Expect userInteraction status to be FINISHED on civicPass rejection action', async () => {
    await testHelpers.simulateCivicPassRejectionAction(GatekeeperAPIStatus.REJECTED);
  });
});
